import Login from "../LoginPage/Login";
import SignUp from "../SignUp/SignUp";
import { useState } from "react";
import "./Auth.css";

function AuthPage()
{
    const [data, setData] = useState({firstName : null, lastName : null, userName : null, password : null});
    const [isSignUp, setSignUp] = useState(true);


    return (
        <div className = "Authentication">
            {isSignUp ? <SignUp /> : <Login />}
            {isSignUp && <h3 onClick={() => setSignUp((prev) => !prev)}>"Already have an account? Log in"</h3>}
            {!isSignUp && <h3 onClick={() => setSignUp((prev) => !prev)}>"Don't have an accoutn yet? Sign up"</h3>}
        </div>
    );
}

export default AuthPage;
